# Peterson, Holly 

start = int(input("Starting number? "))
end = int(input("Ending number? "))

while(end <= start):
    print("Ending number must be greater than",start)
    end = int(input("Ending number? "))

evenCounter = 0 
evenSum = 0

oddCounter = 0 
oddSum = 0

for i in range(start,end + 1):

    if(i%2 == 0 and i != 0):

        evenSum = i + evenSum 
        evenCounter = evenCounter + 1 

    if(i%2 == 1):

        oddSum = i +oddSum
        oddCounter = oddCounter + 1

print("\nIn the range",start,"to",end)

print("There are",evenCounter,"even numbers with a sum of",evenSum,)

print("There are",oddCounter,"odd numbers with a sum of",oddSum,)
