# Peterson, Holly 

def addNumbers (X,Y):
    return( X + Y)

def multiplyNumbers (X,Y):
    return (X * Y)

while(True):

    numberA = int(input("Enter a number(0 to exit): "))
    if(numberA == 0):break

    numberB = int(input("Enter another number: "))
    if(numberB == 0):break

    addedNumbers = addNumbers(numberA, numberB)
    print(numberA,"+",numberB,"=",addedNumbers)

    multipliedNumbers = multiplyNumbers(numberA, numberB)
    print(numberA,"*",numberB,"=", multipliedNumbers)

print("All Done.")
