startingNumber = int ( input("Starting number?") ) 
endingNumber = int ( input("Ending number?") )

while ( endingNumber <= startingNumber):
    print ("Ending number must be greater than", startingNumber)
    endingNumber = int ( input("Ending number?") )
print ( "In the range", startingNumber, "to", endingNumber)
countEven = 0
countOdd = 0
sumEven = 0 
sumOdd = 0
end = int (endingNumber + 1)
for counter in range (startingNumber, end):
    if ( counter == 0): continue
    if ( counter % 2 == 0): 
        countEven = countEven + 1
    else: 
        countOdd = countOdd + 1

    if ( counter % 2 == 0): 
        sumEven = int (sumEven + counter)
    else: 
        sumOdd = int (sumOdd + counter)
print ( "There are", countEven, "even numbers with a sum of ", sumEven)
print ( "There are", countOdd, "odd numbers with a sum of ", sumOdd)
